import React, {useState} from 'react';
import {Plus} from 'lucide-react'
import './QuestionsTab.css'
import {EditText} from 'react-edit-text';
import 'react-edit-text/dist/index.css';
import Question from "./Question/Question";
import { v4 as uuid } from 'uuid';

class MCQ {
    constructor() {
        this.id = uuid();
        this.type = "MCQ"
        this.statement = "Untitled Question"
        this.options = ["Option 1"]
    }
}


class TextBased {
    constructor() {
        this.id = uuid()
        this.type = "TextBased"
        this.statement = "Untitled Question "
    }
}

class Questions {
    constructor() {
        this.questions = []
        this.questions[0] = new TextBased()
    }
}



function QuestionsTab(props) {

    const questionsList = new Questions()

    const [name, setName] = useState("");

    const [description, setDescription] = useState("");

    function changeName(value) {
        console.log(value);
        setName(value.value)
    }


    const [questions, setQuestions] = useState(questionsList.questions);

    function rename(text,index) {
        questions[index].statement = text
        console.log(questions);
    }


    function switchHandler(index) {
        if(index === undefined) {return}
        questions[index].type === "MCQ" ? questions[index] = new TextBased() : questions[index] = new MCQ()
    }

    useEffect(() => {
        return () => {
            console.log(questions);
        };
    }, [questions]);



    function addQuestion() {
        setQuestions([...questions, new TextBased()])
    }

    function deleteQuestion(index) {
        const newQuestions = questions.filter(question=>{
            console.log("questions.indexOf(question) : "+questions.indexOf(question));
            console.log("index : "+index);
            return questions.indexOf(question) !== index
        })
        console.log(newQuestions);
        setQuestions([...newQuestions])
    }

    return (<div>
            <span className={"floating-action"}>
                <Plus onClick={addQuestion} color={"#085B91"} strokeWidth={"4"} size={"24"}/>
            </span>
            <div className={"poll-header"}>
                <EditText defaultValue={name} placeholder={"Poll Name"} onSave={changeName} className={"poll-name"}/>
                <EditText defaultValue={description} placeholder={"Poll Description"} className={"poll-desc"}/>
                {questions.length > 0 && questions.map((question) => (
                    <Question  onDelete={deleteQuestion} id={questions.indexOf(question)} key={question.id} title={question.statement} type={question.type} rename={rename} switch={switchHandler}/>))}
            </div>
        </div>);
}

export default QuestionsTab;
