import React, {useState} from 'react';
import {Plus} from 'lucide-react'
import './QuestionsTab.css'
import {EditText} from 'react-edit-text';
import 'react-edit-text/dist/index.css';
import Question from "./Question/Question";

class MCQ {
    constructor(index) {
        this.type = "MCQ"
        this.statement = "Untitled Question " + index
        this.options = ["Option 1"]
    }
}


class TextBased {
    constructor(index) {
        this.type = "TextBased"
        this.statement = "Untitled Question " + index
    }
}

class Questions {
    constructor() {
        this.questions = []
        this.questions[0] = new TextBased(1)
    }

    switch(index) {
        this.questions[index].type === "MCQ" ? this.questions[index] = new TextBased(index) : this.questions[index] = new MCQ(index);
        return this.questions
    }
}

function QuestionsTab(props) {

    const questionsList = new Questions()

    const [name, setName] = useState("Untitled Poll");

    function changeName(value) {
        value.preventDefault();
        setName(value)
    }

    const [questions, setQuestions] = useState(questionsList.questions);


    function addQuestion() {
        setQuestions([...questions, new TextBased(questions.length + 1)])
    }

    return (<div>
            <span className={"floating-action"}>
                <Plus onClick={addQuestion} color={"#085B91"} strokeWidth={"4"} size={"24"}/>
            </span>
            <div className={"poll-header"}>
                <EditText defaultValue={name} onSave={changeName} className={"poll-name"}/>
                <EditText defaultValue={"hello"} className={"poll-desc"}/>
                {questions.length > 0 && questions.map((question) => (
                    <Question  key={question.index} title={question.statement} type={question.type} switch={questionsList.switch}/>))}
            </div>
            <button title={"delete"} />
        </div>);
}

export default QuestionsTab;
