import React, {useEffect, useState} from 'react';
import Choice from "./Choice/Choice";
import AddChoice from "./Choice/AddChoice";

function MultipleChoice(props) {

    const [options, setOptions] = useState(props.question.options);
    const [length, setLength] = useState(props.question.options.length);

    function addOption() {
        const newOption = props.onAddOption(props.id, "Option")
        setOptions([...options,newOption])
    }

    function deleteOption(index) {
        const newOptions = props.onDeleteOption(props.id,index)
        console.log(props.id);
        console.log(newOptions);
        setOptions([...newOptions])
    }

    useEffect(() => {
        return () => {
            setLength(options.length)
            console.log("length");
            console.log(length);
        };
    }, [options]);


    return (
        <div>
            {options && options.length > 0 && options.map((option) => (
                <Choice index={options.indexOf(option)} onDelete={deleteOption} length={length} key={option.id} value={option.text} name={"id"}/>
            ))}
            <AddChoice onAddOption={addOption} />
        </div>
    );
}

export default MultipleChoice;
