function styleInject(css, ref) {
  if ( ref === void 0 ) ref = {};
  var insertAt = ref.insertAt;

  if (!css || typeof document === 'undefined') { return; }

  var head = document.head || document.getElementsByTagName('head')[0];
  var style = document.createElement('style');
  style.type = 'text/css';

  if (insertAt === 'top') {
    if (head.firstChild) {
      head.insertBefore(style, head.firstChild);
    } else {
      head.appendChild(style);
    }
  } else {
    head.appendChild(style);
  }

  if (style.styleSheet) {
    style.styleSheet.cssText = css;
  } else {
    style.appendChild(document.createTextNode(css));
  }
}

export default styleInject;
import React from 'react';
import { DropDownProps } from './types';
import './Dropdown.css';

const DropDown: React.FC<DropDownProps> = ({
  children,
  trigger,
  handleClick,
  handleMouseLeave,
  handleMouseOver,
  dropdownMenuRef,
  show,
  style,
  dropdownRef,
  makeDisappear,
  displayMenuItem,
  dropdownWrapperClassName,
  dropdownMenuClassName,
}) => (
  <div
    className={`UnopdropDown_EMFQP${
      dropdownWrapperClassName ? ` ${dropdownWrapperClassName}` : ''
    }`}
    onMouseLeave={handleMouseLeave}
    onMouseOver={handleMouseOver}
    onFocus={() => {}}
    role="button"
    tabIndex={0}
    ref={dropdownRef}
  >
    {React.cloneElement(trigger, {
      onClick: handleClick,
      show: displayMenuItem,
      hide: makeDisappear,
      open: show,
    })}

    <div
      className={`drop-down-menu_EMFQP${
        show ? ' reveal-drop-down-menu_EMFQP' : ''
      }${dropdownMenuClassName ? ` ${dropdownMenuClassName}` : ''}`}
      style={style}
      ref={dropdownMenuRef}
    >
      {typeof children === 'function'
        ? (children as Function)({
            show: displayMenuItem,
            hide: makeDisappear,
            open: show,
          })
        : children}
    </div>
  </div>
);

export default DropDown;
import React from 'react';

export interface CommonProps {
  trigger: JSX.Element;
  dropdownWrapperClassName?: string;
  dropdownMenuClassName?: string;
  children:
    | React.ReactNode
    | ((value: {
        show: (e?: any) => void;
        hide: (e?: any) => void;
        open: boolean;
      }) => React.ReactNode);
}

export type CustomMouseEvent =
  | MouseEvent
  | React.MouseEvent<HTMLDivElement, MouseEvent>;

export interface DropDownProps extends CommonProps {
  handleClick: (e: CustomMouseEvent) => void;
  handleMouseOver: (e: CustomMouseEvent) => void;
  handleMouseLeave: (e: CustomMouseEvent) => void;
  show: boolean;
  style: React.CSSProperties;
  dropdownMenuRef: React.RefObject<HTMLDivElement>;
  dropdownRef: React.RefObject<HTMLDivElement>;
  makeDisappear: (e?: CustomMouseEvent) => void;
  displayMenuItem: (e?: CustomMouseEvent) => void;
}

export enum DropDowndirections {
  RIGHT = 'RIGHT',
  LEFT = 'LEFT',
  CENTER = 'CENTER',
}

export interface UnopDropdownProps extends CommonProps {
  align?: DropDowndirections;
  onDisappearStart?: (e?: any) => void;
  onAppear?: (e?: any) => void;
  onDisappear?: (e?: any) => void;
  delay?: number;
  hover?: boolean;
  closeOnClickOut?: boolean;
  closeOnDropdownClicked?: boolean;
}

export interface TriggerProps {
  onClick: (e: CustomMouseEvent) => void;
  show: (e?: CustomMouseEvent | undefined) => void;
  hide: (e?: CustomMouseEvent | undefined) => void;
  open: boolean;
}
import { DropDowndirections } from './types';

export module Utility {
  export const getStyleObject = (
    align: DropDowndirections,
    width: number
  ): React.CSSProperties => {
    const style: React.CSSProperties = {};
    if (align === DropDowndirections.RIGHT) {
      style.right = 0;
    } else if (align === DropDowndirections.CENTER) {
      style.left = `50%`;
      style.marginLeft = `-${width / 2}px`;
    } else {
      style.left = 0;
    }
    return style;
  };
}
import React, { useState, useRef, useEffect } from 'react';
import DropDown from './Dropdown';
import {
  UnopDropdownProps,
  DropDowndirections,
  CustomMouseEvent,
  TriggerProps,
} from './types';
import { Utility } from './functions.module';

const UnopDropdown: React.FC<UnopDropdownProps> = ({
  trigger,
  children,
  align = DropDowndirections.RIGHT,
  onAppear,
  onDisappear,
  onDisappearStart,
  delay,
  hover,
  closeOnClickOut = false,
  closeOnDropdownClicked = false,
  dropdownWrapperClassName,
  dropdownMenuClassName,
}) => {
  const [show, setShow] = useState(false);

  const timer = useRef<ReturnType<typeof setTimeout> | null>(null);

  const dropdownWidth = useRef(0);

  const dropdownMenuRef = useRef<HTMLDivElement>(null);

  const dropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const element = dropdownMenuRef.current!;
    element.style.visibility = 'hidden';
    element.style.display = 'block';
    dropdownWidth.current = element.offsetWidth;
    element.style.display = 'none';
    element.style.visibility = 'visible';
  }, []);

  const handler = (e: MouseEvent) => {
    const path = e.composedPath();
    if (show && closeOnClickOut && !path.includes(dropdownMenuRef.current!)) {
      handleAction(e);
    } else {
      if (
        show &&
        closeOnDropdownClicked &&
        path.includes(dropdownMenuRef.current!)
      ) {
        handleAction(e);
      }
    }
  };

  useEffect(() => {
    if (closeOnClickOut || closeOnDropdownClicked) {
      window.addEventListener('click', handler);
    }
    return () => {
      window.removeEventListener('click', handler);
    };
  }, [show]);

  const displayMenuItem = (e?: CustomMouseEvent) => {
    if (timer) clearTimeout(timer.current!);
    timer.current = null;
    setShow(true);
    if (onAppear) onAppear(e);
  };

  const makeDisappear = (e?: CustomMouseEvent) => {
    const timerFunc = () =>
      setTimeout(() => {
        setShow(false);
        if (onDisappear) onDisappear(e);
      }, delay || 0);
    timer.current = timerFunc();
    if (onDisappearStart) onDisappearStart(e);
  };

  const handleAction = (e: CustomMouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (show) {
      makeDisappear(e);
    } else {
      displayMenuItem(e);
    }
  };

  const handleMouseOver = (e: CustomMouseEvent) => {
    if (hover && !show) {
      handleAction(e);
    }
  };

  const handleMouseLeave = (e: CustomMouseEvent) => {
    if (hover && show) {
      handleAction(e);
    }
  };

  return (
    <DropDown
      handleMouseOver={handleMouseOver}
      handleMouseLeave={handleMouseLeave}
      handleClick={handleAction}
      show={show}
      trigger={trigger}
      style={Utility.getStyleObject(align, dropdownWidth.current)}
      dropdownMenuRef={dropdownMenuRef}
      dropdownRef={dropdownRef}
      makeDisappear={makeDisappear}
      displayMenuItem={displayMenuItem}
      dropdownWrapperClassName={dropdownWrapperClassName}
      dropdownMenuClassName={dropdownMenuClassName}
    >
      {children}
    </DropDown>
  );
};

export { UnopDropdownProps, DropDowndirections, TriggerProps };

export default UnopDropdown;
