import React, {useState} from 'react';
import {Plus} from 'lucide-react'
import './QuestionsTab.css'
import { EditText } from 'react-edit-text';
import 'react-edit-text/dist/index.css';
import Question from "./Question/Question";
import UnopDropdown from "unop-react-dropdown";
import question from "./Question/Question";

class MCQ{
    constructor(index) {
        this.statement="Untitled Question "+index
        this.options=["Option 1"]
    }
}


class TextBased{
    constructor(index) {
        this.statement="Untitled Question "+index
    }
}

class Questions {
    constructor() {
        this.questions = []
    }

    add(type){
        console.log(this.questions.length);
        if(type==="mcq"){
            this.questions.push(new MCQ(this.questions.length))
        }
        if(type==="text-based"){
            this.questions.push(new TextBased(this.questions.length))
        }
    }
}

function QuestionsTab(props) {

    const [name, setName] = useState("Untitled Poll");
    function changeName(value){
        value.preventDefault();
        setName(value)
    }

    const [questions, setQuestions] = useState([new Questions(mcq)]);

    return (
        <div>
            <span className={"floating-action"} >
                <Plus color={"#085B91"} strokeWidth={"4"} size={"24"} />
            </span>
            <div className={"poll-header"} >
                <EditText defaultValue={name} onSave={changeName} className={"poll-name"} />
                <EditText defaultValue={"hello"} className={"poll-desc"} />
                <Question/>
            </div>
        </div>
    );
}

export default QuestionsTab;
