import React, {useEffect, useState} from 'react';
import Choice from "./Choice/Choice";
import AddChoice from "./Choice/AddChoice";

function MultipleChoice(props) {

    const [options, setOptions] = useState(props.question.options);

    function addOption() {
        setOptions(props.onAddOption(props.id, "Option"+(props.question.options.length+1)))
        console.log("options");
        console.log(options);
    }

    return (
        <div>
            {options && options.length > 0 && options.map((option,index) => (
                <Choice  key={props.question.id} value={props.question.options[index]+" "+(index+1)} name={"id"}/>
            ))}
            <AddChoice question={props.question} onAddOption={addOption} />
        </div>
    );
}

export default MultipleChoice;
