import React, {useEffect, useState} from 'react';
import {EditText} from "react-edit-text";
import 'react-edit-text/dist/index.css';
import './Question.css'
import AnimatedDropdown from "./Animated DropDown/AnimatedDropDown";
import 'react-dropdown/style.css';
import ShortText from "./ShortText/ShortText";
import MultipleChoice from "./MultipleChoice/MultipleChoice";
import {fadeIn} from "react-animations";
import styled, {keyframes} from "styled-components";

function Question(props) {

    const [name, setName] = useState(props.title);

    function changeName(value) {
        value.preventDefault();
        setName(value)
    }

    const [selected, setSelected] = useState(props.type);

    console.log((props.type.name));
    
    const HandleSelection = (val) => {
        setSelected(val)
    }

    useEffect(() => {
        return () => {
            fetch('http://localhost:9000/polls/create/rename-poll', {
                method: "POST", headers: {
                    'Content-Type': 'application/json', boyd: JSON.stringify({
                        name: name.value,
                    })
                }
            })
        };
    }, [name]);


    useEffect(() => {
        console.log(selected)
    }, [selected])

    useEffect(() => {
        console.log(selected)
    }, [selected])


    const FadeIn = styled.div`
      animation: 0.5s ${keyframes(fadeIn)};
    `;

    return (<div className={"question-body"}>
        <div className={"question-heading"}>
            <div>
                <EditText defaultValue={name} onSave={changeName} className={"question-title"}/>
            </div>
            <div className={"dropdown-questions"}>
                <AnimatedDropdown onSelection={HandleSelection}/>
            </div>
        </div>
        <FadeIn>
            <div className={"question-details"}>
                {selected === "Short Text" ? <ShortText/> : <MultipleChoice/>}
            </div>
        </FadeIn>
    </div>);
}

export default Question;
