import {SidebarOpen, Trash, Type} from "lucide-react";
import "./DropDown.css"
import React from "react";
import Popup from "reactjs-popup";
import 'reactjs-popup/dist/index.css';
import {X} from "lucide-react";


export function DropDown(props) {


    function handle(e) {

    }

    return (<div id="myDropdown" className={"dropdown-content"}>
        <a > Remove <Trash size={"10"}/> </a>

        <Popup trigger={<a > Rename <Type size={"10"}/> </a>} position="right center">
            {close => (
            <div className={"overlay"} >
            <div className={"popup"} >
                <div className={"popup-head"} >
                    <X className={"close"} onClick={close} color={"#063651"} size={"20"} />
                    <h4  >
                        Rename Poll
                    </h4>
                </div>

                <form className={"popup-form"} >
                    <input onChange={(e)=>{handle(e)}} value={props.text} type={"text"} />
                    <button type={"submit"}> Save </button>
                </form>
            </div>
            </div>
            )}
        </Popup>

        <a > Open in new Tab <SidebarOpen size={"10"}/></a>
    </div>);

}
