import React, {useState} from 'react';
import {EditText} from "react-edit-text";
import "./Choice.css"
import $ from 'jquery'
function Choice(props) {

    const [name, setName] = useState(props.value);

    function changeName(value){
        value.preventDefault();
        setName(value)
    }

    function deleteOption(){
        console.log(props.index);
        if (props.index !== 0) props.onDelete(props.index)
        else $('delete-option').visibility(0);
    }

    return (
        <div className={"radio-option row"} >

            <label className="container">
                <input disabled={true} type="radio" value={props.value} name={props.name} />
                    <span className="checkmark"/>
            </label>
            <EditText inline={true} defaultValue={name} placeholder={props.value} onSave={changeName} className={"radio-text"} />
            <a id={"delete-option"} onClick={deleteOption} >Delete</a>
        </div>
    );
}

export default Choice;
