import React, {useEffect, useState} from 'react';
import Choice from "./Choice/Choice";
import AddChoice from "./Choice/AddChoice";

function MultipleChoice(props) {

    const [options, setOptions] = useState(props.question.options);

    function addOption() {
        setOptions([...options,(props.onAddOption(props.id,"Option")).last()])
        setOptions(props.onAddOption(props.id, "Option"))
        console.log("options");
        console.log(options);
    }

    return (
        <div>
            {options && options.length > 0 && options.map((option) => (
                <Choice key={option.id} value={option.text} name={"id"}/>
            ))}
            <AddChoice onAddOption={addOption} />
        </div>
    );
}

export default MultipleChoice;
